items.example:
.container {
  display: grid;
  grid-template-columns: 1fr 2.5fr;
  gap: 4rem;
  align-items: center;
}

/* Large visual on right, smaller text on left */
.layout {
  display: grid;
  grid-template-columns: clamp(300px, 35%, 400px) 1fr;
  gap: 3rem;
}

.card-stack {
  position: relative;
  height: 500px;
}

.card {
  position: absolute;
  width: 300px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.15);
  border-radius: 8px;
}

.card:nth-child(1) {
  left: 0;
  top: 0;
  z-index: 3;
}

.card:nth-child(2) {
  left: 60px;
  top: 40px;
  z-index: 2;
}

.card:nth-child(3) {
  left: 120px;
  top: 80px;
  z-index: 1;
}

/* Alternative: using grid-column and margin */
.overlapping-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  margin: 0 -1rem;
}

.card { margin: 0 1rem; z-index: calc(4 - var(--index)); }

.diagonal-section {
  position: relative;
  overflow: hidden;
  background: linear-gradient(135deg, #f5f5f5 0%, #ffffff 100%);
}

/* Skewed background shape */
.diagonal-bg {
  position: absolute;
  width: 150%;
  height: 150%;
  background: var(--accent-color);
  transform: skew(-10deg) rotate(-5deg);
  opacity: 0.1;
  top: -10%;
  left: -10%;
  z-index: 0;
}

.diagonal-content {
  position: relative;
  z-index: 1;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 3rem;
  align-items: center;
  padding: 6rem 4rem;
}

/* Diagonal element arrangement */
.element {
  position: relative;
}

.element:nth-child(odd) {
  transform: rotate(-2deg) skewX(-3deg);
  margin-top: 2rem;
}

.element:nth-child(even) {
  transform: rotate(2deg) skewX(3deg);
  margin-top: -2rem;
}

.grid-container {
  display: grid;
  grid-template-columns: repeat(12, 1fr);
  gap: 2rem;
  position: relative;
  margin: 4rem;
}

/* Standard grid items */
.grid-item {
  grid-column: span 4;
  position: relative;
}

/* Breaking out of grid bounds */
.breakout-large {
  position: absolute;
  width: 150%;
  height: 120%;
  top: -10%;
  left: -25%;
  z-index: 10;
  /* Image or graphic that overlaps adjacent grid items */
}

/* Full-width section overlay */
.breakout-full {
  position: absolute;
  width: 100vw;
  left: 50%;
  transform: translateX(-50%);
  background: var(--emphasis-color);
  z-index: 5;
}

.circular-grid {
  position: relative;
  width: 600px;
  height: 600px;
  /* Central container point */
}

.item {
  position: absolute;
  width: 120px;
  height: 120px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  /* Positioned via transforms */
}

/* Center item */
.item:nth-child(1) {
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%) scale(1.2);
  z-index: 10;
}

/* Orbit positioning for surrounding items */
.item:nth-child(n+2) {
  left: 50%;
  top: 50%;
  /* Calculate rotation and radius */
}

/* Formula: rotate(angle) translateX(radius) */
.item:nth-child(2) { transform: translate(-50%, -50%) rotate(0deg) translateX(150px) rotate(0deg); }
.item:nth-child(3) { transform: translate(-50%, -50%) rotate(72deg) translateX(150px) rotate(-72deg); }
.item:nth-child(4) { transform: translate(-50%, -50%) rotate(144deg) translateX(150px) rotate(-144deg); }
.item:nth-child(5) { transform: translate(-50%, -50%) rotate(216deg) translateX(150px) rotate(-216deg); }
.item:nth-child(6) { transform: translate(-50%, -50%) rotate(288deg) translateX(150px) rotate(-288deg); }

/* CSS Grid Masonry */
.masonry {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  grid-auto-rows: 250px;
  gap: 2rem;
  grid-auto-flow: dense; /* Fills gaps efficiently */
}

.item {
  background: var(--item-bg);
  border-radius: 8px;
  overflow: hidden;
}

/* Size variations */
.item:nth-child(2n) {
  grid-row: span 2;
}

.item:nth-child(3n) {
  grid-column: span 2;
}

/* CSS Columns fallback */
.masonry-columns {
  column-count: 3;
  column-gap: 2rem;
  column-width: 300px;
}

.item {
  break-inside: avoid;
  margin-bottom: 2rem;
}

.split-layout {
  display: grid;
  grid-template-columns: 1fr 1fr;
  min-height: 100vh;
  overflow: hidden;
}

.left-pane {
  position: sticky;
  top: 0;
  height: 100vh;
  overflow-y: auto;
  background: var(--bg-left);
}

.right-pane {
  overflow-y: auto;
  height: 100vh;
  background: var(--bg-right);
}

/* Parallax effect with transform */
.parallax-element {
  position: absolute;
  will-change: transform;
  /* JavaScript updates: transform: translateY(scrollOffset * 0.5) */
}

/* Alternative: using animation-timeline */
.right-pane {
  animation: scroll-parallax linear forwards;
  animation-timeline: view();
}
